import React, { useState, useEffect } from "react";
import { Card, ListGroup } from "react-bootstrap";
import axios from "axios";

function OrderDetails(props) {
    const [isError, setIsError] = useState(false);
    const [status, setStatus] = useState("");
    const [total, setTotal] = useState(0);
    const [owner, setOwner] = useState("");
    const [createdAt, setCreatedAt] = useState("");
    const [product, setProduct] = useState({});
    const [transaction, setTransaction] = useState({});

    useEffect(() => {
        axios.get(`http://localhost:4000/orders/${props.match.params.id}`)
        .then(res => {
            setStatus(res.data.order.status);
            setTotal(res.data.order.total);
            setOwner(res.data.order.owner.username);
            setCreatedAt(res.data.order.createdAt);
            setProduct(res.data.order.product);
            setTransaction(res.data.transaction);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props]);

    return (
        <div className="text-center">
            <h2>Order Details</h2>
                <Card>
                    <Card.Body>
                        <ListGroup>
                            <ListGroup.Item>Total: ${total.toFixed(2)}</ListGroup.Item>
                            <ListGroup.Item>Status: {status}</ListGroup.Item>
                            <ListGroup.Item>Owner: {owner}</ListGroup.Item>
                            <ListGroup.Item>Created At: {createdAt}</ListGroup.Item>
                            <ListGroup.Item>Product details: {JSON.stringify(product)}</ListGroup.Item>
                            <ListGroup.Item>Transaction details: {JSON.stringify(transaction.details)}</ListGroup.Item>
                        </ListGroup>
                    </Card.Body>
                </Card>
            { isError &&<p>Something went wrong with getting the order!</p> }
        </div>
    )
}

export default OrderDetails;
