import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CardColumns, Card, Row, Col, ListGroup } from "react-bootstrap";
import axios from "axios";

function OrderList(props) {
    const [isError, setIsError] = useState(false);
    const [orders, setOrders] = useState([]);

    const Order = props => (
        <Card>
            <Row>
                <Col md={{ span: 4 }}>
                    <Card.Title className="mt-4 text-center">
                        <Link to={`/orders/list/${props.order._id}/`}>{props.order.name}</Link>
                    </Card.Title>
                </Col>
                <Col md={{ span: 8 }}>
                <ListGroup>
                    <ListGroup.Item className="text-truncate">{props.order.description}</ListGroup.Item>
                    <ListGroup.Item>{props.order.stock} in stock</ListGroup.Item>
                </ListGroup>
                </Col>
            </Row>
        </Card>
    )

    useEffect(() => {
        axios.get("http://localhost:4000/orders/")
        .then(res => {
            setOrders(res.data);
        }).catch(function(err) {
            setIsError(true);
        })
    }, []);

    function orderList() {
        return orders.map((currentOrder, i) => {
            return <Order order={currentOrder} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>Orders List</h2>
            <CardColumns>
                { orderList() }
            </CardColumns>
            { isError &&<p>Something went wrong with getting the orders!</p> }
        </div>
    )
}

export default OrderList;
