import React from "react";
import { PayPalButton } from "react-paypal-button-v2";
import axios from "axios";

function PayPalBtn(props) {
  const productInfo = props.productInfo;
  const userID = props.id
  const username = props.username
  let orderID;

    return (
        <PayPalButton
          createOrder={(data, actions) => {
            axios.post("http://localhost:4000/orders/new",
                {productInfo, userID, username}
            ).then((res) => {
                if(res.status === 200) {
                    console.log(res.data);
                    orderID = res.data._id;
                }
            }).catch((err) => {
                console.log(err);
            });
            return actions.order.create({
              purchase_units: [{
                amount: {
                  currency_code: "USD",
                  value: props.amount
                }
              }]
            })
          }}

          onSuccess={(details, data) => {
            alert("Transaction completed by " + details.payer.name.given_name);

            return axios.post("http://localhost:4000/orders/success",
              {data, productInfo, orderID}
            ).then((res) => {
                if(res.status === 200) {
                  console.log(res.data);
                }
            }).catch((err) => {
              console.log(err);
            });
          }}

          options={{clientId: "Aab0HytBSMmwfskLkqetKsqoauh5OeKUp-nMUib58D9Tr4be-Mv7RRU5ws0L2i71D6Cky1MZwRZi55hu"}}
        />
      );
}

export default PayPalBtn;
