import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Table } from "react-bootstrap";
import axios from "axios";

function OrderList(props) {
    const [isError, setIsError] = useState(false);
    const [orders, setOrders] = useState([]);

    const Order = props => (
        <tr>
            <td><Link to={`/users/${props.order.owner.id}/`}>{props.order.owner.username}</Link></td>
            <td>${props.order.status}</td>
            <td>{props.order.createdAt}</td>
            <td>
                <Link to={`/orders/${props.order._id}/edit/`}>Edit</Link>
            </td>
            <td>
                <Link to={`/orders/${props.order._id}/delete/`}>Delete</Link>
            </td>
        </tr>
    )

    useEffect(() => {
        axios.get("http://localhost:4000/orders/")
        .then(res => {
            setOrders(res.data);
        }).catch(function(err) {
            setIsError(true);
        })
    }, []);

    function orderList() {
        return orders.map((currentOrder, i) => {
            return <Order order={currentOrder} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>Orders List</h2>
            <Table striped bordered style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Owner</th>
                        <th>Status</th>
                        <th>Created At</th>
                    </tr>
                </thead>
                <tbody>
                    { orderList() }
                </tbody>
            </Table>
            { isError &&<p>Something went wrong with getting the orders!</p> }
        </div>
    )
}

export default OrderList;
