import React, { useState, useEffect } from "react";
import { Row, Col, Card } from "react-bootstrap";
import axios from "axios";

function UserDetails(props) {
    const [isError, setIsError] = useState(false);
    const [username, setUsername] = useState("");
    const [isAdmin, setIsAdmin] = useState(false);
    const [messages, setMessages] = useState([]);

    const Message = props => (
        <Card>
            <Card.Body>
                <Card.Text>
                    {props.message.content}
                    {IDTokens === props.message.owner.id || adminTokens ? (
                        <p>
                            <Link  to={`/products/list/${id}/messages/${props.message._id}/edit/`} style={{ marginRight: 10 }}>
                                Edit
                            </Link>
                            <Link to={`/products/list/${id}/messages/${props.message._id}/delete/`}>Delete</Link>
                        </p>
                    ) : (
                        <span></span>
                    )}
                </Card.Text>
            </Card.Body>
        </Card>
    )

    useEffect(() => {
        axios.get(`http://localhost:4000/users/${props.match.params.id}`)
        .then(res => {
            setUsername(res.data.user.username);
            setIsAdmin(res.data.user.isAdmin);
            setMessages(res.data.messages);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props]);

    function messageList() {
        return messages.map(function(currentMessage, i){
            return <Message user={currentMessage} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>{username} {isAdmin ? ( "(Admin)" ) : ( "(User)" )}</h2>

            <Row>
                <Col md={{ span: 6 }}>
                    { messageList() }
                </Col>
                <Col md={{ span: 6 }}>
                    (Orders should go here when user is logged in)
                </Col>
            </Row>
            { isError &&<p>Something went wrong with getting the user!</p> }
        </div>
    )
}

export default UserDetails;
