import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Table, Form, Col } from "react-bootstrap";
import lodash from "lodash";
import axios from "axios";

function OrderList(props) {
    const [isError, setIsError] = useState(false);
    const [orders, setOrders] = useState([]);
    const [selectTerm, setSelectTerm] = useState("");

    const Order = props => (
        <tr>
            <td><Link to={`/users/${props.order.owner.id}/`}>{props.order.owner.username}</Link></td>
            <td>{props.order.status}</td>
            <td>{props.order.createdAt}</td>
            <td>${props.order.total.toFixed(2)}</td>
            <td>{props.order.product.name}</td>
            <td>${props.order.product.price.toFixed(2)}</td>
            <td>{props.order.product.quantity}</td>
        </tr>
    )

    useEffect(() => {
        axios.get("http://localhost:4000/orders/")
        .then(res => {
            setOrders(res.data);
        }).catch(function(err) {
            setIsError(true);
        })
    }, []);

    function orderList() {
        return orders.map((currentOrder, i) => {
            return <Order order={currentOrder} key={i} />;
        })
    }

    function handleChange() {
        return lodash.sortBy(orders, ["name", "status"]);
    }

    return (
        <div className="text-center">
            <h2>Orders List</h2>
            <Form>
                <Form.Row>
                    <Form.Group as={Col} sm={{ span: 6 }}>
                        <Form.Label htmlFor="formSelect">Order By</Form.Label>
                        <Form.Control
                                    controlid="formSelect"
                                    as="select"
                                    value={selectTerm}
                                    onChange={e => {
                                        setSelectTerm(e.target.value);
                                        handleChange();
                                    }}
                                    >
                            <option>Status: Completed</option>
                            <option>Status: Cancelled</option>
                        </Form.Control>
                    </Form.Group>
                </Form.Row>
            </Form>
            <Table striped bordered style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Owner</th>
                        <th>Status</th>
                        <th>Created At</th>
                        <th>Total</th>
                        <th colSpan={3}>Product (Name, Price, Quantity)</th>
                    </tr>
                </thead>
                <tbody>
                    { orderList() }
                </tbody>
            </Table>
            { isError &&<p>Something went wrong with getting the orders!</p> }
        </div>
    )
}

export default OrderList;
