import React, { useState, useEffect } from "react";
import { useID, useAdmin } from "../../context/auth";
import { Row, Col, Card, ListGroup } from "react-bootstrap";
import axios from "axios";

function UserDetails(props) {
    const [isError, setIsError] = useState(false);
    const [id, setID] = useState("");
    const [username, setUsername] = useState("");
    const [isAdmin, setIsAdmin] = useState(false);
    const [messages, setMessages] = useState([]);
    const [orders, setOrders] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [messagesPerPage, setMessagesPerPage] = useState(10);
    const { IDTokens } = useID();
    const { adminTokens } = useAdmin();

    const Message = props => (
        <Card>
            <Card.Body>
                <Card.Text>
                    {props.message.content}
                </Card.Text>
            </Card.Body>
        </Card>
    )

    const Order = props => (
        <Card>
                <Card.Header>{props.order.status} Order</Card.Header>
                <ListGroup variant="flush">
                    <ListGroup.Item>Product Name: {props.order.product.name}</ListGroup.Item>
                    <ListGroup.Item>Product Price: ${props.order.product.price.toFixed(2)}</ListGroup.Item>
                    <ListGroup.Item>Product Quantity: {props.order.product.quantity}</ListGroup.Item>
                    <ListGroup.Item>Order Total: ${props.order.total.toFixed(2)}</ListGroup.Item>
                </ListGroup>
        </Card>
    )

    useEffect(() => {
        axios.get(`http://localhost:4000/users/${props.match.params.id}`)
        .then(res => {
            setID(res.data.user._id)
            setUsername(res.data.user.username);
            setIsAdmin(res.data.user.isAdmin);
            setMessages(res.data.messages);
            setOrders(res.data.orders);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props]);

    function messageList() {
        return messages.map(function(currentMessage, i) {
            return <Message message={currentMessage} key={i} />;
        })
    }

    function orderList() {
        return orders.map(function(currentOrder, i) {
            return <Order order={currentOrder} key={i} />;
        })
    }

    function handleClick(e) {
        setCurrentPage(Number(e.target.id))
    }

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(messages.length / messagesPerPage); i++) {
        pageNumbers.push(i);
    }

    function renderPageNumbers() {
        return pageNumbers.map((number) => {
            <li
              key={number}
              id={number}
              onClick={handleClick}
            >
              {number}
            </li>
        })
    }

    const indexOfLastMessage = currentPage * messagesPerPage;
    const indexOfFirstMessage = indexOfLastMessage - messagesPerPage;
    const currentMessages = messages.slice(indexOfFirstMessage, indexOfLastMessage);

    const renderMessages = currentMessages.map((todo, index) => {
        return <li key={index}>{todo}</li>;
    });

    return (
        <div className="text-center">
            <h2>{username} {isAdmin ? ( "(Admin)" ) : ( "(User)" )}</h2>

            <Row>
                <Col md={{ span: 6 }}>
                    <h3>Active Messages</h3>
                    {messages.length > 0 ? (
                        <div>
                            <ul>
                                {renderMessages}
                            </ul>
                            <ul id="page-numbers">
                                {renderPageNumbers}
                            </ul>
                        </div>
                    ) : (
                        <p>(No active messages)</p>
                    )}
                </Col>
                <Col md={{ span: 6 }}>
                    <h3>Active Orders</h3>
                    {orders.length > 0 ? (
                        IDTokens === id || adminTokens ? (
                            orderList()
                        ) : (
                            <p>(You do not have access to this user's orders)</p>
                        )
                    ) : (
                        <p>(No active orders)</p>
                    )}
                </Col>
            </Row>
            { isError &&<p>Something went wrong with getting the user details!</p> }
        </div>
    )
}

export default UserDetails;
