import React, { Component } from "react";
import { Col, Form, Button } from "react-bootstrap";
import axios from "axios";

class ProductCreate extends Component {

    constructor(props) {
        super(props);

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeStock = this.onChangeStock.bind(this);        
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: "",
            description: "",
            price: 0,
            stock: 0
        }
    }

    onChangeName(e) {
        this.setState({
            name: e.target.value
        });
    }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }

    onChangePrice(e) {
        this.setState({
            price: e.target.value
        });
    }

    onChangeStock(e) {
        this.setState({
            stock: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const newProduct = {
            name: this.state.name,
            description: this.state.description,
            price: this.state.price,
            stock: this.state.stock
        };

        axios.post("http://localhost:4000/products/add", newProduct)
            .then(res => console.log(res.data));
        
        this.setState({
            name: "",
            description: "",
            price: 0,
            stock: 0
        })

        this.props.history.push("/");
    }

    render() {
        return (
            <div className="text-center">
                <h2>Create Product</h2>
                <Form onSubmit={this.onSubmit}>
                    <Form.Row>
                        <Form.Group as={Col} sm={{ span: 6 }}>
                            <Form.Label htmlFor="formName">Name</Form.Label>
                            <Form.Control
                                        controlid="formName"
                                        type="text" 
                                        value={this.state.name}
                                        onChange={this.onChangeName}
                                        placeholder="Enter name"
                                        />
                        </Form.Group>
                        <Form.Group as={Col} sm={{ span: 6 }}>
                            <Form.Label htmlFor="formDescription">Description</Form.Label>
                            <Form.Control
                                        controlid="formDescription"
                                        type="text" 
                                        value={this.state.description}
                                        onChange={this.onChangeDescription}
                                        placeholder="Enter description"
                                        />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} sm={{ span: 4 }}>
                            <Form.Label htmlFor="formPrice">Price</Form.Label>
                            <Form.Control
                                        controlid="formPrice"
                                        type="number"
                                        min="0"
                                        max="9999"
                                        step=".01"
                                        value={this.state.price}
                                        onChange={this.onChangePrice}
                                        placeholder="Enter price"
                                        />
                        </Form.Group>
                        <Form.Group as={Col} sm={{ span: 4, offset: 4 }}>
                            <Form.Label htmlFor="formStock">Stock</Form.Label>
                            <Form.Control
                                        controlid="formStock"
                                        type="number"
                                        min="0"
                                        max="9999"
                                        step="1"
                                        value={this.state.stock}
                                        onChange={this.onChangeStock}
                                        placeholder="Enter stock"
                                        />
                        </Form.Group>
                    </Form.Row>
                    <Button variant="success" type="submit">
                        Create Product
                    </Button>
                </Form>
            </div>
        )
    }
}

export default ProductCreate;
