import React, { useState, useEffect } from "react";
import { Card, ListGroup } from "react-bootstrap";
import axios from "axios";

function OrderDetails(props) {
    const [isError, setIsError] = useState(false);
    const [status, setStatus] = useState("");
    const [total, setTotal] = useState(0);
    const [transactionDetails, setTransactionDetails] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:4000/orders/${props.match.params.id}`)
        .then(res => {
            console.log("res.data: ", res.data);
            setStatus(res.data.order.status);
            setTotal(res.data.order.total);
            setTransactionDetails(res.data.transaction.details);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props]);

    const TransactionDetail = props => (
        <ListGroup>
            <ListGroup.Item>Order ID: {props.transactionDetail.orderID}</ListGroup.Item>
            <ListGroup.Item>Payer ID: {props.transactionDetail.payerID}</ListGroup.Item>
            <ListGroup.Item>Facilitator Access Token: {props.transactionDetails.facilitatorAccessToken}</ListGroup.Item>
        </ListGroup>
    )

    function transactionDetailsList() {
        return transactionDetails.map(function(detail, i) {
            return <TransactionDetail detail={detail} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>Order Details</h2>
                <Card>
                    <Card.Body>
                        <ListGroup>
                            <ListGroup.Item>Total: {total}</ListGroup.Item>
                            <ListGroup.Item>Status: {status}</ListGroup.Item>
                        </ListGroup>
                        {transactionDetailsList()}
                    </Card.Body>
                </Card>
            { isError &&<p>Something went wrong with getting the order!</p> }
        </div>
    )
}

export default OrderDetails;
