import React, { useState, useEffect } from "react";
import { Row, Col, Card } from "react-bootstrap";
import axios from "axios";

function UserDetails(props) {
    const [isError, setIsError] = useState(false);
    const [username, setUsername] = useState("");
    const [isAdmin, setIsAdmin] = useState(false);
    const [messages, setMessages] = useState([]);
    const [orders, setOrders] = useState([]);    

    const Message = props => (
        <Card>
            <Card.Body>
                <Card.Text>
                    {props.message.content}
                </Card.Text>
            </Card.Body>
        </Card>
    )

    const Order = props => (
        <Card>
            <Card.Body>
                <Card.Text>
                    Product
                    Name: {props.order.product.name}
                    Price: {props.order.product.price}
                    Amount: {props.order.product.amount}
                    Quantity: {props.order.product.quantity}
                    Total: {props.order.total}
                    Status: {props.order.status}
                </Card.Text>
            </Card.Body>
        </Card>
    )

    useEffect(() => {
        axios.get(`http://localhost:4000/users/${props.match.params.id}`)
        .then(res => {
            setUsername(res.data.user.username);
            setIsAdmin(res.data.user.isAdmin);
            setMessages(res.data.messages);
            setOrders(res.data.orders);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props]);

    function messageList() {
        return messages.map(function(currentMessage, i){
            return <Message message={currentMessage} key={i} />;
        })
    }

    function orderList() {
        return orders.map(function(currentOrder, i){
            return <Order order={currentOrder} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>{username} {isAdmin ? ( "(Admin)" ) : ( "(User)" )}</h2>

            <Row>
                <Col md={{ span: 6 }}>
                    <h3>Active Messages</h3>
                    {messages.length > 0 ? (
                        messageList()
                    ) : (
                        <p>(No active messages)</p>
                    )}
                </Col>
                <Col md={{ span: 6 }}>
                    <h3>Active Orders</h3>
                    {orders.length > 0 ? (
                        orderList()
                    ) : (
                        <p>(No active orders)</p>
                    )}
                </Col>
            </Row>
            { isError &&<p>Something went wrong with getting the user!</p> }
        </div>
    )
}

export default UserDetails;
