import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Table, Form, Col } from "react-bootstrap";
import axios from "axios";

function OrderList(props) {
    const [isError, setIsError] = useState(false);
    const [orders, setOrders] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [selectTerm, setSelectTerm] = useState("");
    const [sortTerm, setSortTerm] = useState("");

    const Order = props => (
        <tr>
            <td><Link to={`/users/${props.order.owner.id}/`}>{props.order.owner.username}</Link></td>
            <td>{props.order.status}</td>
            <td>{props.order.createdAt}</td>
            <td>${props.order.total.toFixed(2)}</td>
            <td>{props.order.product.name}</td>
            <td>${props.order.product.price.toFixed(2)}</td>
            <td>{props.order.product.quantity}</td>
        </tr>
    )

    useEffect(() => {
        axios.get("http://localhost:4000/orders/")
        .then(res => {
            setOrders(res.data);
        }).catch(function(err) {
            setIsError(true);
        })
    }, []);

    function orderList() {
        if(selectTerm === "No Filter") {
            if(searchTerm) {
                return orders.filter(order => order.owner.username.includes(searchTerm)).map((currentOrder, i) => {
                    return <Order order={currentOrder} key={i} />;
                });
            } else {

                if(sortTerm) {
                    orders.sort((a, b) => a.price - b.price).map((currentOrder, i) => {
                        return <Order order={currentOrder} key={i} />;
                    });
                } else {
                    return orders.map((currentOrder, i) => {
                        return <Order order={currentOrder} key={i} />;
                    })
                }
            }
        } else {
            return orders.filter(order => order.status.includes(selectTerm)).map((currentOrder, i) => {
                return <Order order={currentOrder} key={i} />;
            });
        }
    }

    return (
        <div className="text-center">
            <h2>Orders List</h2>
            <Form>
                <Form.Row className="justify-content-sm-center">
                    <Form.Group as={Col} sm={{ span: 6 }}>
                        <Form.Label htmlFor="formSearch">Search Orders by Username</Form.Label>
                        <Form.Control
                                    controlid="formSearch"
                                    type="text"
                                    value={searchTerm}
                                    onChange={e => {
                                        setSearchTerm(e.target.value);
                                    }}
                                    placeholder="Order Owner (case sensitive)"
                                    />
                    </Form.Group>
                </Form.Row>
                <Form.Row className="justify-content-sm-center">
                    <Form.Group as={Col} sm={{ span: 3 }}>
                        <Form.Label htmlFor="formSelect">Filter by Status</Form.Label>
                        <Form.Control
                                    controlid="formSelect"
                                    as="select"
                                    onChange={e => {
                                        setSelectTerm(e.target.value);
                                    }}
                                    >
                            <option>No Filter</option>
                            <option>Completed</option>
                            <option>Cancelled</option>
                        </Form.Control>
                    </Form.Group>
                    <Form.Group as={Col} sm={{ span: 6, offset: 3 }}>
                        <Form.Label>Sort by Price</Form.Label>
                        <Form.Control
                                    controlid="formSelect"
                                    as="select"
                                    onChange={e => {
                                        setSelectTerm(e.target.value);
                                    }}
                                    >
                            <option>No Sort</option>
                            <option>Ascending</option>
                            <option>Descending</option>
                        </Form.Control>
                    </Form.Group>
                </Form.Row>
            </Form>
            <Table striped bordered style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Owner</th>
                        <th>Status</th>
                        <th>Created At</th>
                        <th>Total</th>
                        <th colSpan={3}>Product (Name, Price, Quantity)</th>
                    </tr>
                </thead>
                <tbody>
                    { orderList() }
                </tbody>
            </Table>
            { isError &&<p>Something went wrong with getting the orders!</p> }
        </div>
    )
}

export default OrderList;
