import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Table } from "react-bootstrap";
import axios from "axios";

const Product = props => (
    <tr>
        <td>{props.product.name}</td>
        <td>{props.product.description}</td>
        <td>${props.product.price.toFixed(2)}</td>
        <td>{props.product.stock}</td>
        <td>
            <Link to={`/products/${props.product._id}/edit/`}>Admin: Edit</Link>
        </td>
        <td>
            <Link to={`/products/${props.product._id}/delete/`}>Admin: Delete</Link>
        </td>
    </tr>
)

class ProductsList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            products: []
        };
    }

    componentDidMount() {
        axios.get("http://localhost:4000/products/")
            .then(response => {
                this.setState({ products: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    productList() {
        return this.state.products.map(function(currentProduct, i){
            return <Product product={currentProduct} key={i} />;
        })
    }

    render() {
        return (
            <div className="text-center">
                <h2>Products List</h2>
                <Table striped bordered style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Description</th>
                            <th>Price</th>
                            <th>Stock</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.productList() }
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default ProductsList;
