import React, { useState, useEffect } from "react";
import { Row, Col } from "react-bootstrap";
import axios from "axios";

function UserDetails(props) {
    const [isError, setIsError] = useState(false);
    const [username, setUsername] = useState("");
    const [isAdmin, setIsAdmin] = useState(false);

    useEffect(() => {
        axios.get(`http://localhost:4000/users/${props.match.params.id}`)
        .then(res => {
            setUsername(res.data.username);
            setIsAdmin(res.data.isAdmin);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props]);

    return (
        <div className="text-center">
            <h2>{username} {isAdmin ? ( "(Admin)" ) : ( "(User)" )}</h2>

            <Row>
                <Col md={{ span: 4 }}>
                    (Messages should go here)
                </Col>
                <Col md={{ span: 8 }}>
                    (Orders should go here when logged in)
                </Col>
            </Row>
            { isError &&<p>Something went wrong with getting the user!</p> }
        </div>
    )
}

export default UserDetails;
