import React, { Component } from "react";
import { Form, Button, Accordion, Card, ListGroup } from "react-bootstrap";
import axios from "axios";

class ProductDelete extends Component {

    constructor(props) {
        super(props);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: "",
            description: "",
            price: 0,
            stock: 0
        }
    }

    componentDidMount() {
        axios.get(`http://localhost:4000/products/${this.props.match.params.id}`)
            .then(response => {
                this.setState({
                    name: response.data.name,
                    description: response.data.description,
                    price: response.data.price,
                    stock: response.data.stock
                })   
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    onSubmit(e) {
        e.preventDefault();

        const productToDelete = {
            name: this.state.name,
            description: this.state.description,
            price: this.state.price,
            stock: this.state.stock
        };

        axios.delete(`http://localhost:4000/products/${this.props.match.params.id}`, productToDelete)
            .then(res => console.log(res.data));
        
        this.setState({
            name: "",
            description: "",
            price: 0,
            stock: 0
        })

        this.props.history.push("/");
    }

    render() {
        return (
            <div className="text-center">
                <h2>Delete Product</h2>
                <Form onSubmit={this.onSubmit} style={{ marginTop: 20 }}>
                    <p>Are you sure? Deleted products cannot be recovered.</p>
                    <Accordion>
                        <Card>
                            <Card.Header>
                                <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                    Product Info
                                </Accordion.Toggle>
                            </Card.Header>
                            <Accordion.Collapse eventKey="0">
                                <Card.Body>
                                    <ListGroup>
                                        <ListGroup.Item>Name: {this.state.name}</ListGroup.Item>
                                        <ListGroup.Item>Description: {this.state.description}</ListGroup.Item>
                                        <ListGroup.Item>Price: ${this.state.price.toFixed(2)}</ListGroup.Item>
                                        <ListGroup.Item>Stock: {this.state.stock}</ListGroup.Item>
                                    </ListGroup>
                                </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </Accordion>
                    <Button variant="danger" type="submit">
                        Delete Product
                    </Button>
                </Form>
            </div>
        )
    }
}

export default ProductDelete;
