import React, { useState, useEffect } from "react";
import { useID, useAdmin } from "../../context/auth";
import Paginator from "react-hooks-paginator";
import { Row, Col, Card, ListGroup } from "react-bootstrap";
import axios from "axios";

function UserDetails(props) {
    const [isError, setIsError] = useState(false);
    const [id, setID] = useState("");
    const [username, setUsername] = useState("");
    const [isAdmin, setIsAdmin] = useState(false);
    const [messages, setMessages] = useState([]);
    const [orders, setOrders] = useState([]);
    const pageLimit = 5;
    const [offset, setOffset] = useState(0);
    const [currentPage, setCurrentPage] = useState(1);
    const [currentMessages, setCurrentMessages] = useState([]);
    const [currentOrders, setCurrentOrders] = useState([]);
    const { IDTokens } = useID();
    const { adminTokens } = useAdmin();

    const Message = props => (
        <Card>
            <Card.Body>
                <Card.Text>
                    {props.message.content}
                </Card.Text>
            </Card.Body>
        </Card>
    )

    const Order = props => (
        <Card>
                <Card.Header>{props.order.status} Order</Card.Header>
                <ListGroup variant="flush">
                    <ListGroup.Item>Product Name: {props.order.product.name}</ListGroup.Item>
                    <ListGroup.Item>Product Price: ${props.order.product.price.toFixed(2)}</ListGroup.Item>
                    <ListGroup.Item>Product Quantity: {props.order.product.quantity}</ListGroup.Item>
                    <ListGroup.Item>Order Total: ${props.order.total.toFixed(2)}</ListGroup.Item>
                </ListGroup>
        </Card>
    )

    useEffect(() => {
        axios.get(`http://localhost:4000/users/${props.match.params.id}`)
        .then(res => {
            setID(res.data.user._id)
            setUsername(res.data.user.username);
            setIsAdmin(res.data.user.isAdmin);
            setMessages(res.data.messages);
            setOrders(res.data.orders);
            setCurrentMessages(messages.slice(offset, offset + pageLimit));
            setCurrentOrders(orders.slice(offset, offset + pageLimit));
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props, offset, messages, orders]);

    function messageList() {
        return currentMessages.map(function(messages, i) {
            return <Message message={messages} key={i} />;
        })
    }

    function orderList() {
        return currentOrders.map(function(orders, i){
            return <Order order={orders} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>{username} {isAdmin ? ( "(Admin)" ) : ( "(User)" )}</h2>

            <Row>
                <Col md={{ span: 6 }}>
                    <h3>Active Messages</h3>
                    {messageList()}
                    <Paginator
                    totalRecords={messages.length}
                    pageLimit={pageLimit}
                    pageNeighbours={2}
                    setOffset={setOffset}
                    currentPage={currentPage}
                    setCurrentPage={setCurrentPage}
                    />
                </Col>
                <Col md={{ span: 6 }}>
                    <h3>Active Orders</h3>
                    {orders.length > 0 ? (
                        IDTokens === id || adminTokens ? (
                            <div>
                                {orderList()}
                                <Paginator
                                totalRecords={orders.length}
                                pageLimit={pageLimit}
                                pageNeighbours={2}
                                setOffset={setOffset}
                                currentPage={currentPage}
                                setCurrentPage={setCurrentPage}
                                />
                            </div>
                        ) : (
                            <p>(You do not have access to this user's orders)</p>
                        )
                    ) : (
                        <p>(No active orders)</p>
                    )}
                </Col>
            </Row>
            { isError &&<p>Something went wrong with getting the user details!</p> }
        </div>
    )
}

export default UserDetails;
