import React from "react";
import { PayPalButton } from "react-paypal-button-v2";

function PayPalBtn(props) {

    return (
        <PayPalButton
          amount={props.amount}
          // shippingPreference="NO_SHIPPING" // default is "GET_FROM_FILE"
          onSuccess={(details, data) => {
            alert("Transaction completed by " + details.payer.name.given_name);

            return axios.post("http://localhost:4000/orders/createOrder",
                {productInfo, orderInfo}
            ).then((res) => {
                if(res.status === 200) {
                    console.log(res.data);
                } else {
                    console.log(err);
                }
            }).catch((err) => {
                console.log(err);
            })
          }}

          onApprove={(details, data) =>{
            return axios.post("http://localhost:4000/orders/success",
                {orderID: data.orderID}
            ).then((res) => {
                if(res.status === 200) {
                    console.log(res.data);
                } else {
                    console.log(err);
                }
            }).catch((err) => {
                console.log(err);
            })
          }}

          options={{
            clientId: "Aab0HytBSMmwfskLkqetKsqoauh5OeKUp-nMUib58D9Tr4be-Mv7RRU5ws0L2i71D6Cky1MZwRZi55hu"
          }}
        />
      );
}

export default PayPalBtn;
