import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Card } from "react-bootstrap";
import axios from "axios";

function ProductListPublic(props) {
    const [isError, setIsError] = useState(false);
    const [products, setProducts] = useState([]);

    const Product = props => (
        <Card style={{ width: "18rem" }}>
            <Card.Body>
                <Card.Title>{props.product.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{props.product.stock} in stock</Card.Subtitle>
                <Link to={`/products/list/${props.product._id}/`}>Product Details</Link>
            </Card.Body>
        </Card>
    )

    useEffect(() => {
        axios.get("http://localhost:4000/products/")
        .then(res => {
            setProducts(res.data);
        }).catch(function(err) {
            setIsError(true);
        })
    }, []);

    function productList() {
        return products.map(function(currentProduct, i){
            return <Product product={currentProduct} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>Products List</h2>
            <CardColumns>
                { productList() }
            </CardColumns>
            { isError &&<p>Something went wrong with getting the products!</p> }
        </div>
    )
}

export default ProductListPublic;
