import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CardColumns, Card, Row, Col, ListGroup, Form } from "react-bootstrap";
import axios from "axios";

function ProductListPublic(props) {
    const [isError, setIsError] = useState(false);
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [selectTerm, setSelectTerm] = useState("None");

    const Product = props => (
        <Card>
            <Row>
                <Col md={{ span: 4 }}>
                    <Card.Title className="mt-4 text-center">
                        <Link to={`/products/list/${props.product._id}/`}>{props.product.name}</Link>
                    </Card.Title>
                </Col>
                <Col md={{ span: 8 }}>
                <ListGroup>
                    <ListGroup.Item className="text-truncate">{props.product.description}</ListGroup.Item>
                    <ListGroup.Item>{props.product.stock} in stock</ListGroup.Item>
                </ListGroup>
                </Col>
            </Row>
        </Card>
    )

    useEffect(() => {
        axios.get("http://localhost:4000/products/")
        .then(res => {
            setProducts(res.data);
        }).catch(function(err) {
            setIsError(true);
        })
    }, []);

    function productList() {
        if(selectTerm !== "None") {
            return products.filter(product => product.selectTerm).map((currentProduct, i) => {
                return <Product product={currentProduct} key={i} />;
            });
        } else {
            if(searchTerm) {
                return products.filter(product => product.name.includes(searchTerm)).map((currentProduct, i) => {
                    return <Product product={currentProduct} key={i} />;
                });
            } else {
                return products.map((currentProduct, i) => {
                    return <Product product={currentProduct} key={i} />;
                })
            }
        }
    }

    return (
        <div className="text-center">
            <h2>Products List</h2>
            <Form>
                <Form.Row className="justify-content-sm-center">
                    {selectTerm === "None" &&
                        <Form.Group as={Col} sm={{ span: 6 }}>
                        <Form.Label htmlFor="formSearch">Search Products</Form.Label>
                        <Form.Control
                                    controlid="formSearch"
                                    type="text"
                                    value={searchTerm}
                                    onChange={e => {
                                        setSearchTerm(e.target.value);
                                    }}
                                    placeholder="Product Name (case sensitive)"
                                    />
                        </Form.Group>
                    }
                </Form.Row>
                <Form.Row className="justify-content-sm-center">
                    <Form.Group  as={Col} sm={{ span: 2 }}>
                        <Form.Label htmlFor="formSelect">Search By:</Form.Label>
                        <Form.Control
                                    controlid="formSelect"
                                    as="select"
                                    value={selectTerm}
                                    onChange={e => {
                                        setSelectTerm(e.target.value);
                                    }}
                                    >
                            <option>None</option>
                            <option>Name</option>
                            <option>Stock</option>                            
                        </Form.Control>
                    </Form.Group>
                </Form.Row>
            </Form>
            <CardColumns>
                { productList() }
            </CardColumns>
            { isError &&<p>Something went wrong with getting the products!</p> }
        </div>
    )
}

export default ProductListPublic;
