import React, { useState } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { AuthContext, RoleContext } from "./context/auth";
import "bootstrap/dist/css/bootstrap.min.css";

/* Begin Import Components */
import NavigationBar from "./components/navbar";
import Public from "./components/public-components/public";
import Private from "./components/private-components/private";
import Admin from "./components/admin-components/admin";
/* End Import Components */

function App(props) {
  const [authTokens, setAuthTokens] = useState();
  const [roleTokens, setRoleTokens] = useState();

  const setTokens = (data) => {
    localStorage.setItem("tokens", JSON.stringify(data));
    setAuthTokens(data);
  }

  const setRole = (data) => {
    if(data) {
      if(data === true) {
        localStorage.setItem("role", "Admin");
      } else {
        localStorage.setItem("role", "User");
      }
    }
    setRoleTokens(data);
  }

  return (
    <AuthContext.Provider value={{ authTokens, setAuthTokens: setTokens}}>
      <RoleContext.Provider value={{ roleTokens, setRoleTokens: setRole}}>
        <Router>
          <NavigationBar />
          <div className="container">
            <Public />
            <Private />
            <Admin />
          </div>
        </Router>
      </RoleContext.Provider>
    </AuthContext.Provider>
  );
}

export default App;
