import React, { useState } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { AuthContext } from "./context/auth";
import "bootstrap/dist/css/bootstrap.min.css";

import PrivateRoute from './privateRoute';
import AdminRoute from './adminRoute';

/* Begin Import Components */
import NavigationBar from "./components/navbar.component";
import Landing from "./components/public-components/landing.component";
import Contact from "./components/public-components/contact.component";
import Private from "./components/private-components/private";
import Admin from "./components/admin-components/admin";
import ProductComponent from "./components/product-components/product.component";
import UserComponent from "./components/user-components/user.component";
/* End Import Components */

function App(props) {
  const [authTokens, setAuthTokens] = useState();

  const setTokens = (data) => {
    localStorage.setItem("tokens", JSON.stringify(data));
    setAuthTokens(data);
  }

  return (
    <AuthContext.Provider value={{ authTokens, setAuthTokens: setTokens }}>
      <Router>
        <NavigationBar />
        <div className="container">
          {/* Begin Routes */}
            <Route path="/" exact component={Landing} />
            <Route path="/contact" exact component={Contact} />
            <PrivateRoute path="/private" component={Private} />
            <AdminRoute path="/admin" component={Admin} />
            <UserComponent />
            <ProductComponent />
          {/* End Routes */}
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
