import React, { useState } from "react";
import { Link, Redirect } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../../context/auth";
import { Col, Form, Button } from "react-bootstrap";

function Register(props) {
    const [isLoggedIn, setLoggedIn] = useState(false);
    const [isError, setIsError] = useState(false);
    const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const { setAuthTokens } = useAuth();
    const referer = props.location.state.referer || '/';

    function postRegister() {
        axios.post("/users/add", {
            userName,
            password
        }).then(res => {
            if (res.status === 200) {
                setAuthTokens(res.data);
                setLoggedIn(true);
            } else {
                setIsError(true);
            }
        }).catch(err => {
            setIsError(true);
        });
    }

    if (isLoggedIn) {
        return <Redirect to={referer} />;
    }

    return (
        <div className="text-center">
            <h2>Register</h2>
            <Form>
                <Form.Row>
                    <Form.Group as={Col} sm={{ span: 6 }}>
                        <Form.Label htmlFor="formUsername">Username</Form.Label>
                        <Form.Control
                                    controlid="formUsername"
                                    type="text" 
                                    value={userName}
                                    onChange={e => {
                                        setUserName(e.target.value);
                                    }}
                                    placeholder="Enter username"
                                    />
                    </Form.Group>
                    <Form.Group as={Col} sm={{ span: 6 }}>
                        <Form.Label htmlFor="formPassword">Password</Form.Label>
                        <Form.Control
                                    controlid="formPassword"
                                    type="password" 
                                    value={password}
                                    onChange={e => {
                                        setPassword(e.target.value);
                                    }}
                                    placeholder="Enter password"
                                    />
                    </Form.Group>
                </Form.Row>
                <Button onClick={postRegister} variant="success">Login</Button>
            </Form>
            <Link to="/login">Already have an account?</Link>
            { isError &&<p>The username already exists!</p> }
        </div>
    )
}

export default Register;
