import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useID, useUserName, useAdmin } from "../../context/auth";
import { PayPalButton } from "react-paypal-button-v2";
import { Card, ListGroup, Form, Col } from "react-bootstrap";
import axios from "axios";

function ProductDetails(props) {
    const [isError, setIsError] = useState(false);
    const [id, setID] = useState("");
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState(0);
    const [stock, setStock] = useState(0);
    const [quantity, setQuantity] = useState(0);
    const [messages, setMessages] = useState([]);
    const { IDTokens } = useID();
    const { userNameTokens } = useUserName();
    const { adminTokens } = useAdmin();

    useEffect(() => {
        axios.get(`http://localhost:4000/products/${props.match.params.id}`)
        .then(res => {
            setID(res.data.product._id);
            setName(res.data.product.name);
            setDescription(res.data.product.description);
            setPrice(res.data.product.price);
            setStock(res.data.product.stock);
            setMessages(res.data.messages);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props, IDTokens]);

    const Message = props => (
        <Card>
            <Card.Body>
                <Card.Title>
                    {props.message.owner.username === "(User removed)" ? (
                        <span>{props.message.owner.username}</span>
                    ) : (
                        <Link to={`/users/${props.message.owner.id}`}>{props.message.owner.username}</Link>                        
                    )}
                </Card.Title>
                <Card.Text>
                    {props.message.content}
                </Card.Text>
                {IDTokens === props.message.owner.id || adminTokens ? (
                    <span>
                        <Link  to={`/products/list/${id}/messages/${props.message._id}/edit/`} style={{ marginRight: 10, marginLeft: 10 }}>
                            Edit
                        </Link>
                        <Link to={`/products/list/${id}/messages/${props.message._id}/delete/`}>Delete</Link>
                    </span>
                ) : (
                    <span></span>
                )}
            </Card.Body>
        </Card>
    )

    function messageList() {
        return messages.map(function(currentMessage, i) {
            return <Message message={currentMessage} key={i} />;
        })
    }

    function paypalBtn() {
        return <PayPalButton
                    amount={price * quantity}
                    // shippingPreference="NO_SHIPPING" // default is "GET_FROM_FILE"
                    createOrder={(data, actions) => {
                        let productInfo = {
                            id,
                            name,
                            description,
                            price,
                            quantity
                        };
            
                        let orderInfo = {
                            owner: {
                                id: IDTokens,
                                username: userNameTokens
                            },
                            status: "Created"
                        };

                        return (
                            actions.order.create({
                                purchase_units: [
                                    {
                                        description: description,
                                        amount: {
                                            currency_code: 'USD',
                                            value: price * quantity,
                                        },
                                    },
                                ],
                            }),

                            axios.post("http://localhost:4000/orders/createOrder",
                                {productInfo, orderInfo}
                            ).then((res) => {
                                if(res.status === 200) {
                                    console.log(res.data);
                                } else {
                                    setIsError(true);
                                }
                            }).catch((err) => {
                                setIsError(true);
                            })
                        )
                    }}
                    onSuccess={(details, data) => {
                        alert("Transaction completed by " + details.payer.name.given_name);

                        // OPTIONAL: Call your server to save the transaction
                        return axios.post("http://localhost:4000/orders/success",
                            {orderID: data.orderID}
                        ).then((res) => {
                            if(res.status === 200) {
                                console.log(res.data);
                            } else {
                                setIsError(true);
                            }
                        }).catch((err) => {
                            setIsError(true);
                        })
                    }}
                    options={{
                        clientId: "Aab0HytBSMmwfskLkqetKsqoauh5OeKUp-nMUib58D9Tr4be-Mv7RRU5ws0L2i71D6Cky1MZwRZi55hu"
                    }}
                />
    }

    return (
        <div className="text-center">
            <h2>Products Details</h2>
                <Card>
                    <Card.Body>
                        <ListGroup>
                            <ListGroup.Item>Name: {name}</ListGroup.Item>
                            <ListGroup.Item>Description: {description}</ListGroup.Item>
                            <ListGroup.Item>Price: ${price.toFixed(2)}</ListGroup.Item>
                            <ListGroup.Item>Stock: {stock}</ListGroup.Item>
                        </ListGroup>
                        {stock > 0 && IDTokens ? (
                            <Form>
                                <h2>Order This Product</h2>
                                <Form.Row>
                                    <Form.Group as={Col} sm={{ span: 6, offset: 3 }}>
                                        <Form.Label htmlFor="formQuantity">Quantity</Form.Label>
                                        <Form.Control
                                                    controlid="formQuantity"
                                                    type="number"
                                                    min="1"
                                                    max="5"
                                                    step="1"
                                                    onChange={e => {
                                                        setQuantity(e.target.value);
                                                    }}
                                                    placeholder="Enter quantity to order (max 5)"
                                                    />
                                    </Form.Group>
                                </Form.Row>
                                { quantity > 0 && paypalBtn()}
                            </Form>
                        ) : (
                            "Cannot order, currently out of stock or user is not currently logged in"
                        )}
                    </Card.Body>
                </Card>
            <Link to={`/products/list/${id}/messages/new`}>Questions or Comments Regarding this Product? Leave a Message.</Link>
            <h3>Messages: </h3>
            {messages.length > 0 ? (
                messageList()
            ) : (
                <p>(No messages)</p>
            )}
            { isError &&<p>Something went wrong with getting the product or placing the order!</p> }
        </div>
    )
}

export default ProductDetails;
