import React, { useState, useEffect } from "react";
import { useID, useAdmin } from "../../context/auth";
import { Row, Col, Card, ListGroup } from "react-bootstrap";
import axios from "axios";

function UserDetails(props) {
    const [isError, setIsError] = useState(false);
    const [id, setID] = useState("");
    const [username, setUsername] = useState("");
    const [isAdmin, setIsAdmin] = useState(false);
    const [messages, setMessages] = useState([]);
    const [orders, setOrders] = useState([]);

    const [resultsPerPage, setResultsPerPage] = useState(5);
    const [numberofMessages, setNumberofMessages] = useState(0);
    const [pages, setPages] = useState(0);
    const [currentPage, setCurrentPage] = useState(0);

    const { IDTokens } = useID();
    const { adminTokens } = useAdmin();

    const Message = props => (
        <Card>
            <Card.Body>
                <Card.Text>
                    {props.message.content}
                </Card.Text>
            </Card.Body>
        </Card>
    )

    const Order = props => (
        <Card>
            <Card.Header>{props.order.status} Order</Card.Header>
            <ListGroup variant="flush">
                <ListGroup.Item>Product Name: {props.order.product.name}</ListGroup.Item>
                <ListGroup.Item>Product Price: ${props.order.product.price.toFixed(2)}</ListGroup.Item>
                <ListGroup.Item>Product Quantity: {props.order.product.quantity}</ListGroup.Item>
                <ListGroup.Item>Order Total: ${props.order.total.toFixed(2)}</ListGroup.Item>
            </ListGroup>
        </Card>
    )

    useEffect(() => {
        axios.get(`http://localhost:4000/users/${props.match.params.id}`)
        .then(res => {
            setID(res.data.user._id)
            setUsername(res.data.user.username);
            setIsAdmin(res.data.user.isAdmin);
            setMessages(res.data.messages);
            setOrders(res.data.orders);

            setNumberofMessages(messages.length());
            setPages(Math.ceil(numberofMessages / resultsPerPage))

        }).catch(function(err) {
            setIsError(true);
        })
    }, [props, messages]);

    function messageList() {
        return messages.map(function(currentMessage, i){
            return <Message message={currentMessage} key={i} />;
        })
    }

    function orderList() {
        return orders.map(function(currentOrder, i){
            return <Order order={currentOrder} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>{username} {isAdmin ? ( "(Admin)" ) : ( "(User)" )}</h2>

            <Row>
                <Col md={{ span: 6 }}>
                    <h3>Active Messages</h3>
                    {messages.length() > 0 ? (
                        <div>
                            {messageList()}
                            <ul class="pagination">
                                {currentPage === 1 && pages > 1 ? (
                                    <ul class="pagination">
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                Page {parseInt(currentPage) + 1} &gt;
                                            </a>
                                        </li>
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                Last Page ({parseInt(pages)}) &gt;&gt;
                                            </a>
                                        </li>
                                    </ul>
                                ) : currentPage < pages ? (
                                    <ul class="pagination">
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                &lt;&lt; First Page (1)
                                            </a>
                                        </li>
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                &lt; Page {parseInt(currentPage) - 1}
                                            </a>
                                        </li>
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                Page {parseInt(currentPage) + 1} &gt;
                                            </a>
                                        </li>
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                Last Page ({parseInt(pages)}) &gt;&gt;
                                            </a>
                                        </li>
                                    </ul>
                                ) : currentPage == pages && pages > 1 ? (
                                    <ul class="pagination">
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                &lt;&lt; First Page (1)
                                            </a>
                                        </li>
                                        <li class="page-item">
                                            <a class="page-link" href={``}>
                                                &lt; Page {parseInt(currentPage) - 1}
                                            </a>
                                        </li>
                                    </ul>
                                ): (
                                    <div></div>
                                )}
                            </ul>
                        </div>
                    ) : (
                        <p>(No active messages)</p>
                    )}
                </Col>
                <Col md={{ span: 6 }}>
                    <h3>Active Orders</h3>
                    {orders.length > 0 ? (
                        IDTokens === id || adminTokens ? (
                            orderList()
                        ) : (
                            <p>(You do not have access to this user's orders)</p>
                        )
                    ) : (
                        <p>(No active orders)</p>
                    )}
                </Col>
            </Row>
            { isError &&<p>Something went wrong with getting the user details!</p> }
        </div>
    )
}

export default UserDetails;
