import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

/* Begin Import Components */
import NavigationBar from "./components/general-components/navbar.component";
import Landing from "./components/general-components/landing.component";
import Contact from "./components/general-components/contact.component";
import Register from "./components/user-components/register.component";
import Login from "./components/user-components/login.component";
import Logout from "./components/user-components/logout.component";
import UsersList from "./components/user-components/users-list.component";
import UserDetails from "./components/user-components/user-details.component";
import OrderComponent from "./components/order-components/order-component";
import ProductComponent from "./components/product-components/product.component";
/* End Import Components */

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
        id: "",
        username: "",
        password: "",
        loggedIn: false
    }

    this.updateUser = this.updateUser.bind(this);
  }

  updateUser(userObject, login) {
    this.setState({
      id: userObject.id,
      username: userObject.username,
      password: userObject.password,
      loggedIn: login
    })
  }

  render() {
    return (
      <Router>
        <NavigationBar />
        <h3>{this.state.id}</h3>
        <h3>{this.state.username}</h3>
        <h3>{this.state.password}</h3>
        <div className="container">
          {/* Begin Routes */}
          <Switch>
            <Route path="/" exact component={Landing} />
            <Route path="/contact" exact component={Contact} />
            <Route path="/register" exact component={Register} />
            <Route path="/login" exact render={(props) =><Login {...props} updateUser={this.updateUser} />}/>
            <Route path="/logout" exact render={(props) =><Logout {...props} updateUser={this.updateUser} />}/>
            <Route path="/users/" exact component={UsersList} />
            <Route path="/users/:id" exact component={UserDetails} />
            <OrderComponent></OrderComponent>
            <ProductComponent></ProductComponent>
          </Switch>
          {/* End Routes */}
        </div>
      </Router>
    );
  }
}

export default App;
