import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

/* Begin Import Components */
import Register from "./register.component";
import Login from "./login.component";
import Logout from "./logout.component";
import UsersList from "./users-list.component";
import UserDetails from "./user-details.component";
/* End Import Components */

class UserComponent extends Component {

    constructor(props) {
        super(props);
    
        this.state = {
            session: {},
            id: ""
        }
    
        this.updateUser = this.updateUser.bind(this);
      }
    
      updateUser(session, id) {
        this.setState({
          session: session,
          id: id
        })
      }

      componentDidMount() {
        axios.get("http://localhost:4000/users/logged_in").then(res => {
          this.setState({
            session: res.data,
            id: res.passport._id
          });
          console.log("this.state.session: ");
          console.log(this.state.session);
          console.log("this.state.id: ");
          console.log(this.state.id);
        }).catch(err => {
          console.log(err);
        })
      }

    User() {
        return (
            <Switch>
                {/* Begin User Routes */}
                <Route path="/register" exact component={Register} />
                <Route path="/login" exact render={(props) =><Login {...props} updateUser={this.updateUser} />}/>
                <Route path="/logout" exact render={(props) =><Logout {...props} updateUser={this.updateUser} />}/>
                <Route path="/users/" exact component={UsersList} />
                <Route path="/users/:id" exact component={UserDetails} />
                {/* End User Routes */}
            </Switch>
        )
    }

    render() {
        return (
            this.User()
        );
    }
}

export default UserComponent;
