import React from "react";
import { PayPalButton } from "react-paypal-button-v2";
import axios from "axios";

function PayPalBtn(props) {
  const productInfo = props.productInfo;

    return (
        <PayPalButton
          amount = {props.amount}

          createOrder={(details, data) => {
            return axios.post("http://localhost:4000/orders/new",
                {data, productInfo}
            ).then((res) => {
                if(res.status === 200) {
                    console.log(res.data);
                }
            }).catch((err) => {
                console.log(err);
            });
          }}

          onSuccess={(details, data) => {
            alert("Transaction completed by " + details.payer.name.given_name);

            return axios.post("http://localhost:4000/orders/success",
                {data}
            ).then((res) => {
                if(res.status === 200) {
                    console.log(res.data);
                }
            }).catch((err) => {
                console.log(err);
            });
          }}

          options={{clientId: "Aab0HytBSMmwfskLkqetKsqoauh5OeKUp-nMUib58D9Tr4be-Mv7RRU5ws0L2i71D6Cky1MZwRZi55hu"}}
        />
      );
}

export default PayPalBtn;
