import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

/* Begin Import Components */
import Register from "./register.component";
import Login from "./login.component";
import Logout from "./logout.component";
import UsersList from "./users-list.component";
import UserDetails from "./user-details.component";
/* End Import Components */

class UserComponent extends Component {

    constructor(props) {
        super(props);
    
        this.state = {
          username: "",
          password: "",
          session: {}
        }
    
        this.updateSession = this.updateSession.bind(this);
      }
    
      updateSession(username, password, session) {
        this.setState({
          username: username,
          password: password,
          session: session
        })
      }

      componentDidMount() {
        axios.post("http://localhost:4000/users/login", {
          username: this.state.username,
          password: this.state.password
      }).then(res => {
          this.setState({
            session: res.data
        });
          console.log("axios.get(/logged_in) response: ");
          console.log("this.state.session: ");
          console.log(this.state.session);
        }).catch(err => {
          console.log(err);
        })
      }

    User() {
        return (
            <Switch>
                {/* Begin User Routes */}
                <Route path="/register" exact component={Register} />
                <Route path="/login" exact render={(props) =><Login {...props} updateSession={this.updateSession} />}/>
                <Route path="/logout" exact render={(props) =><Logout {...props} updateSession={this.updateSession} />}/>
                <Route path="/users/" exact component={UsersList} />
                <Route path="/users/:id" exact component={UserDetails} />
                {/* End User Routes */}
            </Switch>
        )
    }

    render() {
        return (
            this.User()
        );
    }
}

export default UserComponent;
