import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CardColumns, Card, Row, Col, ListGroup, Form, Button } from "react-bootstrap";
import axios from "axios";

function ProductListPublic(props) {
    const [isError, setIsError] = useState(false);
    const [products, setProducts] = useState([]);

    const Product = props => (
        <Card>
            <Row>
                <Col md={{ span: 4 }}>
                    <Card.Title className="mt-4 text-center">
                        <Link to={`/products/list/${props.product._id}/`}>{props.product.name}</Link>
                    </Card.Title>
                </Col>
                <Col md={{ span: 8 }}>
                <ListGroup>
                    <ListGroup.Item className="text-truncate">{props.product.description}</ListGroup.Item>
                    <ListGroup.Item>{props.product.stock} in stock</ListGroup.Item>
                </ListGroup>
                </Col>
            </Row>
        </Card>
    )

    const searchBar = (
        <Form>
            <Form.Row>
                <Form.Group as={Col} sm={{ span: 6 }}>
                    <Form.Control
                                controlid="formName"
                                type="text" 
                                value={name}
                                onChange={e => {
                                    setName(e.target.value);
                                }}
                                placeholder="Product Name"
                                />
                    <Form.Append>
                        <Button variant="outline-secondary">Button</Button>
                    </Form.Append>
                </Form.Group>
            </Form.Row>
        </Form>
    )

    useEffect(() => {
        axios.get("http://localhost:4000/products/")
        .then(res => {
            setProducts(res.data);
        }).catch(function(err) {
            setIsError(true);
        })
    }, []);

    function productList() {
        return products.map(function(currentProduct, i){
            return <Product product={currentProduct} key={i} />;
        })
    }

    return (
        <div className="text-center">
            <h2>Products List</h2>
            <CardColumns>
                { productList() }
            </CardColumns>
            { isError &&<p>Something went wrong with getting the products!</p> }
        </div>
    )
}

export default ProductListPublic;
