import React, { useState, useEffect } from "react";
import { Button, Accordion, Card, ListGroup, Row, Col } from "react-bootstrap";
import axios from "axios";

function UserDetails(props) {
    const [isError, setIsError] = useState(false);
    const [username, setUsername] = useState("");
    const [isAdmin, setIsAdmin] = useState(false);
    const [createdAt, setCreatedAt] = useState("");

    useEffect(() => {
        axios.get(`http://localhost:4000/users/${props.match.params.id}`)
        .then(res => {
            setUsername(res.data.username);
            setIsAdmin(res.data.isAdmin);
            setCreatedAt(res.data.createdAt);
        }).catch(function(err) {
            setIsError(true);
        })
    }, [props]);

    return (
        <div className="text-center">
            <h2>User: {username} {isAdmin ? ( "Admin" ) : ( "User" )}</h2>

            <Row>
                <Col md={{ span: 4 }}>
                </Col>
                <Col md={{ span: 4 }}>
                </Col>
            </Row>

            <Accordion>
                <Card>
                    <Card.Header>
                        <Accordion.Toggle as={Button} variant="link" eventKey="0">
                            User Info
                        </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="0">
                        <Card.Body>
                            <ListGroup>
                                <ListGroup.Item>Username: {username}</ListGroup.Item>
                                <ListGroup.Item>Joined: {createdAt}</ListGroup.Item>
                            </ListGroup>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </Accordion>
            { isError &&<p>Something went wrong with getting the user!</p> }
        </div>
    )
}

export default UserDetails;
